function addUserNotification(){var t=$("#notificationUserId").val(),i=$("#notificationMessage").val(),e=$("#notificationType").val();$.post("../api/add_notification.php",{id:t,message:i,type:e},function(i,e,a){$("#notificationModal").modal("hide"),$("#user"+t).highlightAnimation(green,1500)}).fail(function(t){$("#notificationStatus").fadeIn()})}function config(){$("#notificationForm").submit(function(t){t.preventDefault(),addUserNotification()}),$("#notificationStatus").click(function(){$(this).fadeOut()})}$(document).ready(config);
