function removeEvent(t){$.post("../api/delete_event.php",{id:t},function(e,n,i){$("#event"+t).remove()}).fail(function(e){$("#event"+t).highlightAnimation(red,1500)})}function confirmRemoveEvent(){var t=$(this).closest("tr").attr("id");t=t.replace("event",""),$("#confirm").modal({backdrop:"static",keyboard:!1}).one("click","#delete",function(){removeEvent(t)})}function configEditEventModal(){$("#addEventModal form").trigger("reset"),$("#addEventModalTitle").html("Editar Evento");var t=$(this).closest("tr").attr("id");t=t.replace("event","");var e=$("#event"+t+" td:nth-child(1)").text(),n=$("#event"+t+" td:nth-child(2)").text(),i=$("#event"+t+" td:nth-child(3)").text(),d=$("#event"+t+" td:nth-child(4)").text(),a=$("#event"+t+" td:nth-child(5)").text(),l=$("#event"+t+" td:nth-child(6)").text();$("#id").val(t),$("#name").val(n),$("#description").val(e),$("#date").val(i),$("#duration").val(d),$("#place").val(a),$("#price").val(l)}function config(){$('i[data-original-title="Editar"]').click(configEditEventModal),$('i[data-original-title="Eliminar"]').click(confirmRemoveEvent)}$(document).ready(config);
