function registerUser(){var b=$("#identification").val();var d=$("#role").val();var e=$("#email").val();var h=$("#password").val();var a=$("#name").val();var g=$("#gender").val();var k=$("#birthdate").val();var j=$("#nif").val();var i=$("#cellphone").val();var c=$("#paymethod").val();var f=$("#periodicity").val();$.post("../api/register_user.php",{id:b,role:d,email:e,password:h,name:a,gender:g,birth:k,nif:j,cellphone:i,donative_type:c,periodicity:f},function(l,m,n){$("#registerUser").modal("hide");$("#users tr:last").after("<tr><td>"+b+"</td><td>"+a+"</td><td>"+k+"</td><td>"+d+'</td><td><i class="fa fa-pencil fa-lg fa-fw" data-toggle="tooltip" data-original-title="Editar"></i><i class="fa fa-briefcase fa-lg fa-fw" data-toggle="tooltip" data-original-title="Alterar Cargo"></i><i class="fa fa-trash fa-lg fa-fw" data-toggle="tooltip" data-original-title="Eliminar"></i></td></tr>');$("#users tr:last").highlightAnimation("#DFF0D8",1500);enableTooltips()}).fail(function(l){$("#registerStatus").fadeIn()})}function removeUser(){var a=$(this).closest("tr").attr("id");a=a.replace("user","");$.post("../api/delete_user.php",{id:a},function(b,c,d){$("#user"+a).remove()}).fail(function(b){$("#user"+a).highlightAnimation("#A94442",1500)})}function enableTooltips(){$('[data-toggle="tooltip"]').tooltip({placement:"top"})}$.fn.highlightAnimation=function(e,c){var d=e||"#DFF0D8";var a=c||1500;var b=this.css("background-color");this.stop().css("background-color",d).animate({backgroundColor:b},a)};$(document).ready(function(){enableTooltips();$("#registerUserSubmit").click(registerUser);$('i[data-original-title="Eliminar"]').click(removeUser);$("#registerStatus").click(function(){$(this).fadeOut()});$("#role").change(function(){var a=$("#role").val();if(a==="Amigo"){$("#friendOnlyParams").fadeIn()}else{$("#friendOnlyParams").fadeOut()}})});