function registerFriendEvent(){var t=$("#attendanceEventId").val(),e=$("#attendanceUserId").val(),n=$("#attendancePaymentId").val();$.post("../api/add_friend_event.php",{eventId:t,userId:e,paymentId:n},function(e,n,i){""===e?($("#addUserAttendanceEventModal").modal("hide"),$("#event"+t).highlightAnimation(green,1500)):$("#addUserAttendanceEventStatus").fadeIn()}).fail(function(t){$("#addUserAttendanceEventStatus").fadeIn()})}function config(){$("#addUserAttendanceEventForm").submit(function(t){t.preventDefault(),registerFriendEvent()}),$("#addUserAttendanceEventStatus").click(function(){$(this).fadeOut()})}$(document).ready(config);
