function addUserNotification(){var t=$("#notificationUserId").val(),e=$("#notificationMessage").val(),n=$("#notificationType").val();$.post("../api/add_notification.php",{id:t,message:e,type:n},function(e,n,i){""===e?($("#notificationModal").modal("hide"),$("#user"+t).highlightAnimation(green,1500)):$("#notificationStatus").fadeIn()}).fail(function(t){$("#notificationStatus").fadeIn()})}function config(){$("#notificationForm").submit(function(t){t.preventDefault(),addUserNotification()}),$("#notificationStatus").click(function(){$(this).fadeOut()})}$(document).ready(config);
