function registerUser(){var e=$("#identification").val(),r=$("#role").val(),i=$("#email").val(),a=$("#password").val(),n=$("#name").val(),l=$("#gender").val(),o=$("#birthdate").val();$.post("",{id:e,role:r,email:i,password:a,name:n,gender:l,birth:o},function(e){}).fail(function(e){displayError("Error while processing the registration of the new user...")})}function registerFriend(){var e=$("#identification").val(),r=$("#role").val(),i=$("#email").val(),a=$("#password").val(),n=$("#name").val(),l=$("#gender").val(),o=$("#birthdate").val(),t=$("#nif").val(),s=$("#cellphone").val(),d="#paymethod".val(),v="#periodicity".val();$.post("",{id:e,role:r,email:i,password:a,name:n,gender:l,birth:o,nif:t,cellphone:s,donative_type:d,periodicity:v},function(e){}).fail(function(e){displayError("Error while processing the registration of the new user...")})}$(document).ready(function(){$("#registerUserSubmit").click(function(){var e=$("#role").val();"Amigo"===e?registerFriend():registerUser()}),$("#role").change(function(){var e=$("#role").val();"Amigo"===e?$("#friendOnlyParams").fadeIn():$("#friendOnlyParams").fadeOut()})});