function registerUser(){var e=$("#identification").val(),r=$("#role").val(),i=$("#email").val(),a=$("#password").val(),n=$("#name").val(),o=$("#gender").val(),l=$("#birthdate").val();$.post("../api/register_user.php",{id:e,role:r,email:i,password:a,name:n,gender:o,birth:l},function(e,r,i){console.log(i.status)}).fail(function(e){displayError("Error while processing the registration of the new user...")})}function registerFriend(){var e=$("#identification").val(),r=$("#role").val(),i=$("#email").val(),a=$("#password").val(),n=$("#name").val(),o=$("#gender").val(),l=$("#birthdate").val(),t=$("#nif").val(),s=$("#cellphone").val(),d=$("#paymethod").val(),v=$("#periodicity").val();$.post("../api/register_user.php",{id:e,role:r,email:i,password:a,name:n,gender:o,birth:l,nif:t,cellphone:s,donative_type:d,periodicity:v},function(e,r,i){console.log(i.status)}).fail(function(e){displayError("Error while processing the registration of the new user...")})}$(document).ready(function(){$("#registerUserSubmit").click(function(){var e=$("#role").val();"Amigo"===e?registerFriend():registerUser()}),$("#role").change(function(){var e=$("#role").val();"Amigo"===e?$("#friendOnlyParams").fadeIn():$("#friendOnlyParams").fadeOut()})});